if(searchType === &quot;tags&quot;){

	valueOfSearchTerm = &quot;#&quot;+searchTerm; // value of &quot;searchTerm&quot;

	valueOfSearchTerm == &quot;#undefined&quot; ? valueOfSearchTerm = &quot;&quot; : valueOfSearchTerm = valueOfSearchTerm

	searchPagePaths = dv.pages(valueOfSearchTerm).file.path; // paths of all pages with searchTerm

} else if(searchType === &quot;mdate&quot;) {
	valueOfSearchTerm = searchDate; // value of &quot;searchTerm&quot;

	let dateRegex = new RegExp(&quot;[0-9]{4}-[0-9]{2}-[0-9]{2}&quot;)

	let mtimes = dv.pages().where(p =&gt; formatDate(p.file.mtime) == searchDate)

	searchPagePaths = mtimes.file.path;

}

/* ==================== MAKE TABLE ==================== */

let allNames = new Array();
let allWordCount = new Array();
let wordCountSum = new Array();
let allCharCount = new Array();
let charCountSum = new Array()
let allSentenceCount = new Array();
let sentenceCountSum = new Array();
let allMdate = new Array();
let pageCount = new Array();

const getTableContents = async() =&gt; {
	let name = new Array();
	let text = new Array();
	let mtime = new Array();
	let output = new Array()

	// fill text and name with values
	for(let i=0; i &lt; searchPagePaths.length;i++){
		let page = this.app.vault.getAbstractFileByPath(searchPagePaths.values[i]);
		const content = await app.vault.cachedRead(page);
		page != null ? mtime.push(page.stat.mtime) : null;
		page != null ? name.push(&quot;[[&quot;+page.basename+&quot;]]&quot;) : null;
		content != null ? text.push(content) : null;
	};

	mtime.forEach((date, index) =&gt; {
		var d = new Date(date),
        month = &#039;&#039; + (d.getMonth() + 1),
        day = &#039;&#039; + d.getDate(),
        year = d.getFullYear();

		if (month.length &lt; 2) 
        	month = &#039;0&#039; + month;
		if (day.length &lt; 2) 
        	day = &#039;0&#039; + day;

    	mtime[index] = [year, month, day].join(&#039;-&#039;);
	})


	// do word, character, and sentence count on pages and format for dv.table
	for(let i=0; i&lt;text.length;i++){
		// www.tutorialspoint.com/how-to-count-a-number-of-words-in-given-string-in-javascript
		text[i] = text[i].replace(/(^\\s\*)|(\\s\*$)/gi,&quot;&quot;); // remove the start and end spaces of the given string
		text[i] = text[i].replace(/\[ \]{2,}/gi,&quot; &quot;); // reduce multiple spaces to a single space
		text[i] = text[i].replace(/\\n /,&quot;\\n&quot;); // exclude a new line with a start spacing
		let wordCount = getWordCount(text[i]) - 1;
		let characterCount = getCharacterCount(text[i]) - valueOfSearchTerm.length;
		let sentenceCount = getSentenceCount(text[i]);

		allNames.push([name[i]])
		allWordCount.push(wordCount);
		allCharCount.push(characterCount);
		allSentenceCount.push(sentenceCount);
		allMdate.push(mtime[i])


		output.push([[name[i]].toString(), mtime[i], wordCount, characterCount, sentenceCount]); // array with link to file, word count, character count, and sentence count
		};
		pageCount.push(allNames.length)
		wordCountSum.push(allWordCount.reduce((a, b) =&gt; a + b, 0))
		charCountSum.push(allCharCount.reduce((a, b) =&gt; a + b, 0))
		sentenceCountSum.push(allSentenceCount.reduce((a, b) =&gt; a + b, 0))
		output.push([&quot;&lt;strong&gt;Total&lt;/strong&gt;&quot;, &quot;&quot;, wordCountSum.toString(), charCountSum.toString(), sentenceCountSum.toString() ])
		return output;
	}

	dv.table([&quot;File Name&quot;, &quot;Data of Modification&quot;, &quot;Number of Words&quot;,  &quot;Number of Charactors&quot;, &quot;Number of Paragraphes&quot;], await getTableContents()
	)

	if(searchType == &quot;mdate&quot; &amp;&amp; searchPagePaths. length &lt; 1){
		dv.paragraph(&quot;&lt;strong&gt;No Pages Modified On That Date&lt;/strong&gt;&quot;)
	}

